(function(g){function Chart(ctx,cfg){const d=cfg.data.datasets[0],vals=d.data,cols=d.backgroundColor,tot=vals.reduce((a,b)=>a+b,0);const xc=ctx.canvas.width/2,yc=ctx.canvas.height/2,r=Math.min(xc,yc)-2;let s=0;vals.forEach((v,i)=>{const a=2*Math.PI*v/tot;ctx.fillStyle=cols[i%cols.length];ctx.beginPath();ctx.moveTo(xc,yc);ctx.arc(xc,yc,r,s,s+a);ctx.closePath();ctx.fill();s+=a;});}g.Chart=Chart;})(this);